///for text files,
int getSize(string a)
{
    string line;
    char s[100];
    strcpy(s,a.c_str());
    strcat(s,".txt");
    ifstream in(s);
    int i=0;
    if(!getline(in,line))
    {
        return 0;
    }
    else
    {
        while(getline(in,line))
        {
            if(line=="$")
            {
                i++;
            }
        }
    }
    return i;
}

