void iniLocation()
{
    string line;
    for(int i=0;i<147;i++)
    {
        location[i]="\0";
    }
    ifstream in("location.txt");
    int i=0;
    while(getline(in,line))
    {
        location[i]+=line+"\n";
        if(line=="")
        {
            i++;
        }
    }
}

