void showReview(string s,int n)
{
    string line;
                   // int n=getSize(choosenOne);
    for(int i=0;i<100;i++)
    {
        reviews[i]="\0";
    }
    int i=0;
    char c[100];
    strcpy(c,s.c_str());
    strcat(c,".txt");
    ifstream in(c);
    while(getline(in,line))
    {
        if(line=="$")
        {
         i++;continue;
        }
        reviews[i]+=line;
        //cout<<reviews[i]<<endl;
    }
    string temp=reviews[0];
    char a[temp.length()+1];
    strcpy(a,temp.c_str());
    SetWindowText(txtreview[n],a);

}

