void showSearchList(HWND hwnd)
{
    //cout<<z;
    iniLocation();

    DestroyWindow(next);
    DestroyWindow(prev);
    ShowWindow(TXTeditorCh,SW_HIDE);
    ShowWindow(btnET1,SW_HIDE);
    ShowWindow(btnET2,SW_HIDE);
    ShowWindow(btnET3,SW_HIDE);
    //ShowWindow(TXTpeopleCh,SW_HIDE);
    ShowWindow(btnPT1,SW_HIDE);
    ShowWindow(btnPT2,SW_HIDE);
    ShowWindow(btnPT3,SW_HIDE);
    //ShowWindow(btnGoBack,SW_SHOW);
    ShowWindow(btnGoBack,SW_SHOW);
    DestroyWindow(txtresultlocation);
    DestroyWindow(txtresultname);
    DestroyWindow(imglogores);
    for(int j=0;j<30;j++)
    {
        DestroyWindow(resultimg[j]);
    }
    char a[100];
    GetWindowText(TXTsearch,a,100);
    int i=0;
    while(a[i]!='\0')
    {
        i++;
    }
    len=0;bool same=false;int pos;
    for(int j=0;j<147;j++)
    {
        string temp=searchname[j];
        for(int k=0;k<i;k++)
        {
            if((a[k]==temp[k])||(int(a[k])-int(temp[k])== -32) || (int(a[k])-int(temp[k])==32))
            {
                same=true;
            }
            else
            {
                same=false;
                break;
            }
        }

        if(same)
        {
            result[len]=temp;
            len++;
        }
    }

    if(len>1)
    {
       int imgcount=1;
       for(int j=0;j<len;j++)
       {
           string temp="Result/"+result[j]+".bmp";
           char b[temp.length()+1];
           strcpy(b,temp.c_str());
           resultimg[j]=CreateWindow("static","",WS_CHILD|WS_VISIBLE|SS_BITMAP,searchstart+10,45+(15*(imgcount))+((imgcount-1)*(h/6)),w/3,h/5.5,hwnd,NULL,NULL,NULL);
           image=(HBITMAP)LoadImage(NULL,b,IMAGE_BITMAP,w/3,h/5.5,LR_LOADFROMFILE);
           SendMessage(resultimg[j],STM_SETIMAGE,IMAGE_BITMAP,(LPARAM)image);

           if(imgcount>3)
           {
               imgcount=1;
               ShowWindow(resultimg[j],SW_HIDE);
           }
           else
            {
                imgcount++;
           }
       }
    }
    else if(len==1)
    {
        string a=result[0];
        for(int l=0;l<146;l++)
        {
            if(searchname[l]==a)
            {
                pos=l;
                break;
            }
        }
        //cout<<pos<<endl;
        char b[a.length()+1];
        strcpy(b,a.c_str());
        txtresultname=CreateWindow("static",b,WS_CHILD|WS_VISIBLE,searchstart+30+(w/10),45,w/10,20,hwnd,NULL,NULL,NULL);
        a=location[pos];
        char c[a.length()+1];
        strcpy(c,a.c_str());
        txtresultlocation=CreateWindow("static",c,WS_CHILD|WS_VISIBLE|WS_VSCROLL,searchstart+30+(w/10),70,w/5,h/3,hwnd,NULL,NULL,NULL);
        imglogores=CreateWindow("static","",WS_CHILD|WS_VISIBLE|SS_BITMAP,searchstart+10,45,w/10,w/10,hwnd,NULL,NULL,NULL);
        a="logo/"+result[0]+".bmp";
        char d[a.length()+1];
        strcpy(d,a.c_str());
        image=(HBITMAP)LoadImage(NULL,d,IMAGE_BITMAP,w/10,w/10,LR_LOADFROMFILE);
        SendMessage(imglogores,STM_SETIMAGE,IMAGE_BITMAP,(LPARAM)image);
    }
    else
    {
        MessageBox(hwnd,"Error 404, Can't found","Not in list",0);
        SetWindowText(TXTsearch,"");
        resultimg[0]=CreateWindow("static","",WS_CHILD|WS_VISIBLE|SS_BITMAP,searchstart+10,h/2,w/2.5,h/6,hwnd,NULL,NULL,NULL);
    }
    if(len>4)
    {
        next=CreateWindow("button","Next",WS_VISIBLE|WS_CHILD,searchstart+60+2*(btnw/3),h-(h/6),btnw/3,btnw/5,hwnd,(HMENU)900,NULL,NULL);
        prev=CreateWindow("button","Prev",WS_VISIBLE|WS_CHILD,searchstart+40+(btnw/3),h-(h/6),btnw/3,btnw/5,hwnd,(HMENU)901,NULL,NULL);
        ShowWindow(prev,SW_HIDE);
        ShowWindow(next,SW_SHOW);
    }
    image=(HBITMAP)LoadImage(NULL,"",IMAGE_BITMAP,1,1,LR_LOADFROMFILE);
    page=(len/4);
    if(len%4!=0)
    {
        page++;
    }
}

