///----------------------for calculation------------------------------///
void calculate(HWND setName,HWND setPrice,int price,string name,int q)
{
    char ori[10000];char qu[5];
    itoa(q,qu,10);
    GetWindowText(setName,ori,10000);
    string temp=ori;
    temp+=name+" x"+qu+"\n";
    char t[temp.length()+1];
    strcpy(t,temp.c_str());
    SetWindowText(setName,t);
    price*=q;
    char p[10];
    itoa(price,p,10);
    GetWindowText(setPrice,ori,10000);
    temp=ori;
    temp+=p;
    temp+="KS\n";
    char t_p[temp.length()+1];
    strcpy(t_p,temp.c_str());
    SetWindowText(setPrice,t_p);
    total+=price;
}

void showresult(HWND res,HWND setname,HWND setprice,float tx)
{
    char ori[10000];char num[10];char ta[10];
    GetWindowText(setname,ori,10000);
    string temp=ori;
    itoa(tx,ta,10);
    temp+="Tax ";temp+=ta;temp+="%\n";
    char t[temp.length()+1];
    strcpy(t,temp.c_str());
    SetWindowText(setname,t);
    GetWindowText(setprice,ori,10000);
    temp=ori;
    char p_t[10];
    tax=total*(float)(tx/100);
    itoa(tax,p_t,10);
    temp+=p_t;
    temp+="KS\n";
    char p[temp.length()+1];
    strcpy(p,temp.c_str());
    SetWindowText(setprice,p);
    total+=tax;
    itoa(total,num,10);
    strcat(num,"KS");
    SetWindowText(res,num);
}
